(294, 'https://sh-cdn.singulart.?signature=db97c8e418d3c1e92034a1b8f543e8b484490288bba800c0611d7ba896913da2', 'Hydrangea Macrophylla', '56', '40', '粉彩', '水彩', 'Katherine Rhodes Fields', 2022, 'Purple', 79200, '繡球花', 'F'),
(295, 'https://sh-cdn.singulart.?signature=eefa3fe3c1081be98d49bf0d5b4cc6fc69d0e10633b912d006d8dca343acc8b0', 'Digitalis Purpurea', '56', '40', '粉彩', '超現實', 'Katherine Rhodes Fields', 2022, 'Red,Yellow', 79200, '紫花洋地黃', 'F'),
(296, 'https://sh-cdn.singulart.?signature=3943dd092a10f832f987077c025bb40f2dd855add4a33547405563252120c95b', 'Ricinus Communis', '56', '40', '粉彩', '超現實', 'Katherine Rhodes Fields', 2022, 'Green', 79200, '蓖麻', 'F'),
(297, 'https://sh-cdn.singulart.?signature=dde0fb15d660ace6d0d8f89d08b9e314b45051e9e86b8221334c392607b00f6c', 'Pferdekopf_3', '21', '21', '水墨', '印象', 'Karina Plachetka', 2019, 'Black,White', 36400, '水墨畫主要不是為了創造人物，而是關於自然和風景主題的大氣和減少的表現。馬是一個美麗的主題。它們屬於家養動物，非野生動物。', 'F'),
