(329, 'https://sh-cdn.singulart.?signature=59f43b9c52abd0c1e5756759422da415dc70a70588ea95bc1e34a48160fa2e15', 'Autumn leaf fall', '93', '150', '油畫', '印象', 'Dmitry Spiros', 2022, 'Orange,Red', 240800, '秋葉落,鮮豔的色彩、豐富的紋理和色調準確的原始筆觸是您成品的標誌。', 'F'),
(330, 'https://sh-cdn.singulart.?signature=056fe3870b875330239b8ea1512f37128750103c723ff357dcfb770e0b08fc94', 'In meadow Green, oil acrylic painting on canvas', '60', '80', '油畫', '印象', 'Dmitry Spiros', 2016, 'Green,Red', 73800, '在草地上綠色。', 'F'),
